declare module "*.css" {
  const styles: Record<string, string>;
  export default styles;
}

declare module "*.svg" {
  const source: string;
  export default source;
}

declare module "*.png" {
  const source: string;
  export default source;
}

declare module "*.md" {
  const source: string;
  export default source;
}

declare module "*.txt" {
  const source: string;
  export default source;
}
